#!/bin/bash
sed -e '/changed_when: False/{a \ \ \- group_by:\n \ \ \ \ \ \key: oo_nodes_use_{{ (openshift_use_contrail | default(False)) | ternary("'"contrail"'","'"nothing"'") }}\n \ \ \ changed_when: False' -e ':a;n;ba}' /home/openshift-ansible/playbooks/openshift-node/private/additional_config.yml > /tmp/new.yml ; mv /tmp/new.yml /home/openshift-ansible/playbooks/openshift-node/private/additional_config.yml
